package canto.parser;

import canto.lang.*;

/**
 * Based on code generated by jjtree.
 *
 * @author Michael St. Hippolyte
 */
public class ParsedConcurrentCantoBlock extends ConcurrentCantoBlock {

    public ParsedConcurrentCantoBlock(int id) {
        super();
    }

    /** Accept the visitor. **/
    public Object jjtAccept(CantoParserVisitor visitor, Object data) {
        return visitor.visit(this, data);
    }

    void setCatch(Block block) {
        if (block != null) {
            setCatchBlock(block);
        }
    }
}
